import { useState } from "react";
import uniqid from "uniqid";

import TodoItem from "./TodoItem";

export default function TodoList() {
  const [todoList, setTodos] = useState([
    { id: 1, text: "Morning routine", isDone: false },
    { id: 2, text: "Coding", isDone: false },
    { id: 3, text: "Workout", isDone: false },
    { id: 4, text: "Time w fav people", isDone: false },
  ]);

  const onTodoInputBlur = (e) => {
    let todo = {
      id: uniqid(),
      text: e.target.value,
      isDone: false,
    };

    setTodos((state) => [...state, todo]);

    e.target.value = "";
  };

  const deleteTodoItemClickHandler = (id) => {
    setTodos((oldTodos) => oldTodos.filter((todo) => todo.id !== id));
  };

  return (
    <ul>
      <label htmlFor="todo-name">Add Todo</label>
      <input
        type="text"
        id="todo-name"
        onBlur={onTodoInputBlur}
        name="todo"
      ></input>
      {todoList.map((todo) => (
        <TodoItem
          key={todo.id}
          todo={todo}
          id={todo.id}
          onDelete={deleteTodoItemClickHandler}
        />
      ))}
    </ul>
  );
}
